import React from 'react';
import '../App.css'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    container: {
        display: 'grid',
        gridTemplateColumns: 'repeat(12, 1fr)',
        gridGap: theme.spacing(2),
      },
}));

function TwoColumnsGrid() {
  const classes = useStyles();
    return (
      <div className={classes.container}>
        <div style={{ gridColumnEnd: 'span 8' }}>
         bla bla
        </div>
        <div style={{ gridColumnEnd: 'span 4' }}>
            <div className="firstProject">
                <p className="customP">Collaborative Project</p>
                <h3>Finger Food</h3>
                <p className="projectDescription">Social media app for gourmet food fans.</p>
                <p className="projectTechnologies">Vanilla Javascript | Firebase | Git</p>
            </div>
        </div>
    </div>
    );
  }
  
  export default TwoColumnsGrid;
